import { Component } from '@angular/core';

@Component({
  selector: 'app-seats',
  templateUrl: './seats.component.html',
  styleUrls: ['./seats.component.css']
})
export class SeatsComponent {
  lowerSingleSeats = this.generateSeats(12, 'available');
  singleUpperBerthSeats = this.generateSeats(6, 'available');
  doubleLowerBerthSeats = this.generateSeats(6, 'available');
  doubleUpperBerthSeats = this.generateSeats(6, 'available');

  // Function to generate seat objects
  private generateSeats(count: number, status: string): any[] {
    const seats = [];
    for (let i = 1; i <= count; i++) {
      seats.push({ name: `LS${i}`, status: status });
    }
    return seats;
  }

  private generateSUBerth(count: number, status: string): any[] {
    const SUberth = [];
    for (let i = 1; i <= count; i++) {
      SUberth.push({ name: `SUB${i}`, status: status });
    }
    return SUberth;
  }
  private generateDUBerth(count: number, status: string): any[] {
    const DUberth = [];
    for (let i = 1; i <= count; i++) {
      DUberth.push({ name: `DUB${i}`, status: status });
    }
    return DUberth;
  }
  private generateDLBerth(count: number, status: string): any[] {
    const DLberth = [];
    for (let i = 1; i <= count; i++) {
      DLberth.push({ name: `DLB${i}`, status: status });
    }
    return DLberth;
  }

  // Function to toggle seat status
  toggleSeatStatus(seat: any) {
    if (seat.status === 'available' || seat.status === 'selected') {
      seat.status = 'booked';
    } else if (seat.status === 'booked') {
      seat.status = 'selected';
    }
  }

}

<div class="container">
  <h2>Bus Seat Booking</h2>

  <!-- Lower Single Seats -->

  <div class="seats">
  <div class="seat-container">
      <label *ngFor="let seat of lowerSingleSeats" class="seat" [class.booked]="seat.status === 'booked'" [class.available]="seat.status === 'available'" [class.ladies]="seat.status === 'ladies'">
          <input type="checkbox" [disabled]="seat.status === 'booked'" [value]="seat.name" (change)="toggleSeatStatus(seat)">
          {{ seat.name }}
      </label>
  </div>

  <!-- Single Upper Berth Seats -->
  <div class="seat-container">
      <label *ngFor="let seat of singleUpperBerthSeats" class="berth" [class.booked]="seat.status === 'booked'" [class.available]="seat.status === 'available'" [class.ladies]="seat.status === 'ladies'">
          <input type="checkbox" [disabled]="SUBseat.status === 'booked'" [value]="seat.name" (change)="toggleSeatStatus(seat)">
          {{ seat.name }}
      </label>
  </div>

  <!-- Double Lower Berth Seats -->
  <div class="seat-container">
      <label *ngFor="let seat of doubleLowerBerthSeats" class="berth" [class.booked]="seat.status === 'booked'" [class.available]="seat.status === 'available'" [class.ladies]="seat.status === 'ladies'">
          <input type="checkbox" [disabled]="seat.status === 'booked'" [value]="seat.name" (change)="toggleSeatStatus(seat)">
          {{ seat.name }}
      </label>
  </div>

  <!-- Double Upper Berth Seats -->
  <div class="seat-container">
      <label *ngFor="let seat of doubleUpperBerthSeats" class="berth" [class.booked]="seat.status === 'booked'" [class.available]="seat.status === 'available'" [class.ladies]="seat.status === 'ladies'">
          <input type="checkbox" [disabled]="seat.status === 'booked'" [value]="seat.name" (change)="toggleSeatStatus(seat)">
          {{ seat.name }}
      </label>
  </div>
</div>

</div>
