import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators, AbstractControl, ValidationErrors } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';
import { BookingService } from '../booking.service';
import { SeatService } from '../seat.service';
import { Seat } from '../seats/seats.component';

@Component({
  selector: 'app-book-tickets',
  templateUrl: './book-tickets.component.html',
  styleUrls: ['./book-tickets.component.scss'],
})
export class BookTicketsComponent implements OnInit {
  selectedSeats: Seat[] = [];
  bookingForms: FormGroup[] = [];
  sst: string;

  constructor(private bookingService: BookingService, private fb: FormBuilder, private http: HttpClient, private seatService: SeatService, private router: Router, private authService: AuthService) {}

  ngOnInit(): void {
    // Get the selected seats data directly from the service
    this.selectedSeats = this.bookingService.getSelectedSeatsData();
    this.initializeForms();
  }

  initializeForms() {
    this.bookingForms = [];

    function nameValidator(control: AbstractControl): ValidationErrors | null {
      const name = control.value;
      if (!name || name.length < 3) {
        return { 'minLength': true };
      }
      if (!/^[a-zA-Z\s]*$/.test(name)) {
        return { 'invalidChars': true };
      }
      return null;
    }
    this.selectedSeats.forEach((seat) => {

      const isLadiesSeat = seat.seatStatus === 'ladiesSelected';
      
      const form = this.fb.group({
        passengerName: [seat.passengerName || '', [Validators.required, nameValidator]],
        passengerAge: [seat.passengerAge || '', [Validators.required, Validators.min(5), Validators.max(125)]],
        passengerGender: [{ value: 'female', disabled: isLadiesSeat }, Validators.required],
      });
      
      
      if (isLadiesSeat) {
        this.form.get('passengerGender').disable();
      } else {
        this.form.get('passengerGender').enable();
      }
      this.bookingForms.push(form);
    });
  }

  logout(){
    this.authService.logout();
  }

  onSubmit() {
    if (this.bookingForms.every((form) => form.valid)) {
      const passengerDataArray = this.bookingForms.map((form, index) => ({
        seatName: this.selectedSeats[index].name,
        seatPrice: this.selectedSeats[index].seatPrice,
        passengerData: form.value,
      }));

      // Send passengerDataArray to the server or perform any other required actions
      // console.log(this.bookingForms);  
      this.bookingService.confirmSeats(passengerDataArray);
      this.router.navigate(['/summary'])

    }
  }
}
<nav class="navbar navbar-dark bg-primary navbar-expand">
  <ul class="navbar-nav">
    <li class="nav-item" routerLinkActive="active">
      <a class="nav-link" routerLink="/user">User</a>
    </li>
    <li class="nav-item" routerLinkActive="active">
      <a class="nav-link"  (click)="logout()">Logout</a>
    </li>
  </ul>
</nav>

<div class="login-container">
  <div class="container mt-4 border">
    <form
      *ngFor="let form of bookingForms; let i = index"
      [formGroup]="form"
      (ngSubmit)="onSubmit()"
    >
      <div class="form-group nxt-grp">
        Passenger for {{ selectedSeats[i].name }}
      </div>
      <div class="form-group">
        <label for="passengerName{{ i }}">Name:</label>
        <input
          type="text"
          [id]="'passengerName' + i"
          formControlName="passengerName"
          class="form-control"
        />
        <span
          *ngIf="
            !form.get('passengerName')?.valid &&
            form.get('passengerName')?.touched
          "
          class="help-block"
          >Please Enter a Valid Name</span
        >
      </div>
      <div class="form-group">
        <label for="passengerAge{{ i }}">Age:</label>
        <input
          type="number"
          [id]="'passengerAge' + i"
          formControlName="passengerAge"
          class="form-control"
        />
        <span
          *ngIf="
            !form.get('passengerAge')?.valid &&
            form.get('passengerAge')?.touched
          "
          class="help-block"
          >Please Book ticket for Children above 5</span
        >
      </div>
      <div class="form-group">
        <label>Gender:</label><br />
        <div class="form-check form-check-inline">
          <input
            class="form-check-input"
            type="radio"
            [id]="'male' + i"
            value="male"
            [disabled]="bookingForms[i].get('passengerGender').disabled"
            formControlName="passengerGender"
          />
          <label class="form-check-label" [for]="'male' + i">Male</label>
        </div>
        <div class="form-check form-check-inline">
          <input
            class="form-check-input"
            type="radio"
            [id]="'female' + i"
            value="female"
            [disabled]="bookingForms[i].get('passengerGender').disabled"
            formControlName="passengerGender"
          />
          <label class="form-check-label" [for]="'female' + i">Female</label>
        </div>
      </div>

      <button
        type="submit"
        [disabled]="!form.valid"
        class="btn btn-primary"
        *ngIf="i === bookingForms.length - 1"
      >
        Submit
      </button>
    </form>
  </div>
</div>

