import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit {
  signupForm: FormGroup;

  constructor(private formBuilder: FormBuilder) { }

  ngOnInit(): void {
    this.signupForm = this.formBuilder.group({
      username: ['', [Validators.required]],
      password: ['', [Validators.required]],
      confirmPassword: ['', [Validators.required]]
    });
  }

  onSubmit() {
    if (this.signupForm.valid) {
      // Handle signup logic here (e.g., send signup request to a server).
      console.log('Sign Up form submitted');
      console.log('Username: ' + this.signupForm.value.username);
      console.log('Password: ' + this.signupForm.value.password);
      console.log('Confirm Password: ' + this.signupForm.value.confirmPassword);
    }
  }
}
<div class="container mt-5">
  <div class="row">
    <div class="col-md-6 offset-md-3">
      <div class="card">
        <div class="card-header">
          Sign Up
        </div>
        <div class="card-body">
          <form [formGroup]="signupForm" (ngSubmit)="onSubmit()">
            <!-- Username field -->
            <div class="form-group">
              <label for="username">Username</label>
              <input type="text" class="form-control" id="username" formControlName="username" placeholder="Enter username">
            </div>

            <!-- Password field -->
            <div class="form-group">
              <label for="password">Password</label>
              <input type="password" class="form-control" id="password" formControlName="password" placeholder="Password">
            </div>

            <!-- Confirm Password field -->
            <div class="form-group">
              <label for="confirmPassword">Confirm Password</label>
              <input type="password" class="form-control" id="confirmPassword" formControlName="confirmPassword" placeholder="Confirm password">
            </div>

            <!-- Sign Up button -->
            <button type="submit" class="btn btn-primary" [disabled]="!signupForm.valid">Sign Up</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
