import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.scss']
})
export class AdminComponent implements OnInit {
  buses: any []= [];
  selectedBusId: string = '';

  constructor(private http: HttpClient,private seatService) { }
   ngOnInit(): void {
     this.http.get('https://go-travel-42246-default-rtdb.firebaseio.com/busses.json').subscribe((posts: any) => {
      console.log(posts);
      this.buses = Object.values(posts);
     });
   }

   getSeatKeys(bus: any): string[] {
    return Object.keys(bus.seats);
  }
  viewSeats(busId: string) {
    this.selectedBusId = busId;
    this.seatService.setSeatData(this.buses.find(bus => bus.id === busId));
    // Navigate to the seats view here (use Angular Router)
  }

}
