import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { AuthGuardService } from './auth/auth-guard.service';
import { AddBusComponent } from './modules/admin/add-bus/add-bus.component';
import { AdminComponent } from './modules/admin/admin.component';
import { BusSeatComponent } from './modules/admin/bus-seat/bus-seat.component';
import { AdminLoginComponent } from './modules/login/admin-login/admin-login.component';
import { LoginComponent } from './modules/login/login/login.component';
import { SignUpComponent } from './modules/login/sign-up/sign-up.component';
import { BookTicketsComponent } from './modules/user/book-tickets/book-tickets.component';
import { SeatsComponent } from './modules/user/seats/seats.component';
import { SummaryComponent } from './modules/user/summary/summary.component';
import { UserComponent } from './modules/user/user.component';
import { PageNotFoundComponent } from './shared/page-not-found/page-not-found.component';
import { HomeComponent as UserHomeComponent } from './modules/user/home/home.component';
import { HomeComponent as AdminHomeComponent } from './modules/admin/home/home.component';



const routes: Routes = [
  { path: 'login', component: LoginComponent },
  { path: 'signup', component: SignUpComponent},
  { path: 'seat', component: BusSeatComponent, canActivate: [AuthGuardService]},
  { 
    path: 'admin', component: AdminComponent, 
    canActivate: [AuthGuardService], 
    children:[
      {path: 'home', component: AdminHomeComponent},
      {path: 'addBus', component: AddBusComponent},
      {path: 'cancelTickets', component: BusSeatComponent},
    ]
  },
  { 
    path: 'addBus',component: AddBusComponent, 
    canActivate: [AuthGuardService], 
    children:[
      {path: 'home', component: UserHomeComponent},
    ]
  },
  { path: 'user', canActivate: [AuthGuardService],component: UserComponent},
  { path: 'adminLogin', component: AdminLoginComponent},
  { path: 'book', canActivate: [AuthGuardService],component: BookTicketsComponent},
  { path: 'seats', canActivate: [AuthGuardService],component: SeatsComponent},
  { path: 'summary', canActivate: [AuthGuardService],component: SummaryComponent},
  { path: '', redirectTo: '/login', pathMatch: 'full' }, 
  { path: '**', component:PageNotFoundComponent } 
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
