import { Component } from '@angular/core';

@Component({
  selector: 'app-seats',
  templateUrl: './seats.component.html',
  styleUrls: ['./seats.component.css']
})
export class SeatsComponent {
  lowerSingleSeats = this.generateSeats(12, 'available'); // Modify as needed for other seat types

  // Function to generate seat objects
  private generateSeats(count: number, status: string): any[] {
    const seats = [];
    for (let i = 1; i <= count; i++) {
      seats.push({ name: `LS${i}`, status: status });
    }
    return seats;
  }

  // Function to toggle seat status
  toggleSeatStatus(seat: any) {
    if (seat.status === 'available') {
      seat.status = 'booked';
    } else if (seat.status === 'booked') {
      seat.status = 'available';
    }
  }

}
<div class="container">
  <h2>Bus Seat Booking</h2>

  <!-- Lower Single Seats -->
  <h3>Lower Single Seats (12 seats)</h3>
  <div>
      <label *ngFor="let seat of lowerSingleSeats" class="seat" [class.booked]="seat.status === 'booked'" [class.available]="seat.status === 'available'" [class.ladies]="seat.status === 'ladies'">
          <input type="checkbox" [disabled]="seat.status === 'booked'" [value]="seat.name" (change)="toggleSeatStatus(seat)">
          {{ seat.name }}
      </label>
  </div>

  <!-- Add similar sections for other types of seats -->
</div>
<div class="container">
  <h2>Bus Seat Booking</h2>

  <!-- Lower Single Seats -->
  <h3>Lower Single Seats (12 seats)</h3>
  <div class="seat-container">
      <label *ngFor="let seat of lowerSingleSeats" class="seat" [class.booked]="seat.status === 'booked'" [class.available]="seat.status === 'available'" [class.ladies]="seat.status === 'ladies'">
          <input type="checkbox" [disabled]="seat.status === 'booked'" [value]="seat.name" (change)="toggleSeatStatus(seat)">
          {{ seat.name }}
      </label>
  </div>

  <!-- Single Upper Berth Seats -->
  <h3>Single Upper Berth Seats (6 seats)</h3>
  <div class="seat-container">
      <label *ngFor="let seat of singleUpperBerthSeats" class="seat" [class.booked]="seat.status === 'booked'" [class.available]="seat.status === 'available'" [class.ladies]="seat.status === 'ladies'">
          <input type="checkbox" [disabled]="seat.status === 'booked'" [value]="seat.name" (change)="toggleSeatStatus(seat)">
          {{ seat.name }}
      </label>
  </div>

  <!-- Double Lower Berth Seats -->
  <h3>Double Lower Berth Seats (6 seats)</h3>
  <div class="seat-container">
      <label *ngFor="let seat of doubleLowerBerthSeats" class="seat" [class.booked]="seat.status === 'booked'" [class.available]="seat.status === 'available'" [class.ladies]="seat.status === 'ladies'">
          <input type="checkbox" [disabled]="seat.status === 'booked'" [value]="seat.name" (change)="toggleSeatStatus(seat)">
          {{ seat.name }}
      </label>
  </div>

  <!-- Double Upper Berth Seats -->
  <h3>Double Upper Berth Seats (6 seats)</h3>
  <div class="seat-container">
      <label *ngFor="let seat of doubleUpperBerthSeats" class="seat" [class.booked]="seat.status === 'booked'" [class.available]="seat.status === 'available'" [class.ladies]="seat.status === 'ladies'">
          <input type="checkbox" [disabled]="seat.status === 'booked'" [value]="seat.name" (change)="toggleSeatStatus(seat)">
          {{ seat.name }}
      </label>
  </div>
</div>
