import { HttpClient } from '@angular/common/http';
import { ChangeDetectorRef, Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';
import { BookingService } from '../booking.service';
import { SeatService } from '../seat.service';
import { Seat } from '../seats/seats.component';

@Component({
  selector: 'app-bus-seat',
  templateUrl: './bus-seat.component.html',
  styleUrls: ['./bus-seat.component.css']
})
export class BusSeatComponent {
  constructor(
    private seatService: SeatService,
    private bookingService: BookingService,
    private router: Router,
    private authService: AuthService,
    private http: HttpClient,
    private cd: ChangeDetectorRef
  ) {
    setTimeout(() => {
    this.allSeats = this.allSeats.concat(
      this.singleUpperBerthSeats,
      this.doubleLowerBerthSeats,
      this.doubleUpperBerthSeats,
      this.lowerSingleSeats
    );
    this.allSeats2 = this.allSeats2.concat(
      this.doubleLowerBerthSeats,
      this.doubleUpperBerthSeats
    );
  }, 500);
  }

  lowerSingleSeats: any[] = [];
  singleUpperBerthSeats: any[] = [];
  doubleLowerBerthSeats: any[] = [];
  doubleUpperBerthSeats: any[] = [];
  allSeats: any[] = [];
  allSeats2: any[] = [];

  isAdmin: boolean;

  ngOnInit(): void {
    const seatData = this.seatService.getSeatData();
    // console.log(seatData);
    this.isAdmin = this.authService.isAdmin();
    console.log(this.isAdmin);

    for (const seatName in seatData) {
      if (seatData.hasOwnProperty(seatName)) {
        const seat = seatData[seatName];

        if (seatName.startsWith('LS')) {
          this.lowerSingleSeats.push({ name: seatName, ...seat });
        } else if (seatName.startsWith('SUB')) {
          this.singleUpperBerthSeats.push({ name: seatName, ...seat });
        } else if (seatName.startsWith('DLB')) {
          this.doubleLowerBerthSeats.push({ name: seatName, ...seat });
        } else if (seatName.startsWith('DUB')) {
          this.doubleUpperBerthSeats.push({ name: seatName, ...seat });
        }
      }
    }

    // Sort the arrays by seat name numerically
    this.lowerSingleSeats.sort((a, b) => {
      const aNumber = parseInt(a.name.match(/\d+/)[0], 10);
      const bNumber = parseInt(b.name.match(/\d+/)[0], 10);
      return aNumber - bNumber;
    });

    this.singleUpperBerthSeats.sort((a, b) => {
      const aNumber = parseInt(a.name.match(/\d+/)[0], 10);
      const bNumber = parseInt(b.name.match(/\d+/)[0], 10);
      return aNumber - bNumber;
    });

    this.doubleLowerBerthSeats.sort((a, b) => {
      const aNumber = parseInt(a.name.match(/\d+/)[0], 10);
      const bNumber = parseInt(b.name.match(/\d+/)[0], 10);
      return aNumber - bNumber;
    });

    this.doubleUpperBerthSeats.sort((a, b) => {
      const aNumber = parseInt(a.name.match(/\d+/)[0], 10);
      const bNumber = parseInt(b.name.match(/\d+/)[0], 10);
      return aNumber - bNumber;
    });

    setTimeout(() => {}, 300);

    // console.log(this.lowerSingleSeats);
    // console.log(this.singleUpperBerthSeats);
    // console.log(this.doubleLowerBerthSeats);
    // console.log(this.doubleUpperBerthSeats);
  }
  // const seatData = ti
  selectedSeats: Seat[] = [];
  adjSeats: Seat[] = [];
  passengerName: string = '';
  passengerAge: number = 0;
  passengerGender: string = '';

  logout(){
    this.authService.logout();
  }

  toggleSeatStatus(seat: any) {
    if (this.isAdmin){
      return;
    }
    if (seat.seatStatus === 'Available' && this.selectedSeats.length < 5 ) {
      seat.seatStatus = 'selected';
      this.selectedSeats.push(seat);
    } else if (seat.seatStatus === 'ladies' && this.selectedSeats.length < 5 ) {
      seat.seatStatus = 'ladiesSelected';
      this.selectedSeats.push(seat);
    } else if (seat.seatStatus === 'ladiesSelected' && this.selectedSeats.length < 5 ) {
      seat.seatStatus = 'ladies';
      const index = this.selectedSeats.findIndex((s) => s.name === seat.name);
      if (index !== -1) {
        this.selectedSeats.splice(index, 1);
      }
    } else if (seat.seatStatus === 'selected') {
      seat.seatStatus = 'Available';
      const index = this.selectedSeats.findIndex((s) => s.name === seat.name);
      if (index !== -1) {
        this.selectedSeats.splice(index, 1);
      }
    }
  }


  processSeats(seatsArray: any[]) {
    seatsArray.forEach((seat, index, array) => {
      if (seat.seatStatus === 'selected') {
        const adjacentSeatIndex = index + 1 <= 6 ? index + 6 : index - 6;
        if (array[adjacentSeatIndex].seatStatus !== 'booked') {
          this.adjSeats.push(array[adjacentSeatIndex]);
        }
      }
    });
  }

  getSeatClass(seatStatus: string): { [key: string]: boolean } {
    return {
      booked: seatStatus === 'booked',
      Available: seatStatus === 'Available',
      selected: seatStatus === 'selected',
      ladies: seatStatus === 'ladies',
      ladiesSelected: seatStatus === 'ladiesSelected',
      gents: seatStatus === 'gents',
      gentsSelected: seatStatus === 'gentsSelected',
    };
  }
  

  bookSelectedSeats() {
    if (this.selectedSeats.length === 0) {
      return;
    }
    this.processSeats(this.doubleLowerBerthSeats);
    this.processSeats(this.doubleUpperBerthSeats);
    this.bookingService.bookSeats(this.selectedSeats, this.adjSeats);
    this.router.navigate(['/book']);
    // console.log(this.selectedSeats);
  }

  adjacent(seatname: string, seatprice: number){

    const seatData = {
      name: seatname,
      passengerDetails: {
        passengerName: '',
        passengerAge: '',
        passengerGender: '',
      },
      seatPrice: seatprice,
      seatStatus: 'Available',  
    };

    this.allSeats2.forEach((seat, index, array) => {
      if (seat.name === seatname) {
        const adjacentSeatIndex = ((index%12) + 1 <= 6 ? index + 6 : index - 6);
        if (array[adjacentSeatIndex].seatStatus === 'ladies') {
          const data = {
            seatStatus: 'Available',
            seatPrice: array[adjacentSeatIndex].seatPrice,
            passengerDetails: {
              passengerName: '',
              passengerAge: '',
              passengerGender: '',
            },
          };
          
          const busId = this.seatService.getBusData();
          this.http
            .put(
              'https://go-travel-42246-default-rtdb.firebaseio.com/busses/-' +
                busId +
                '/seats/' +
                array[adjacentSeatIndex].name +
                '.json',
              data
            ).subscribe(
              ()=>{
                if(seatname.startsWith('DUB')){
                  this.doubleUpperBerthSeats.forEach((seat, index, array) => {
                    if(seat.name === seatname){
                      array[index] = seatData;
                    }
                  });
                }
                if(seatname.startsWith('DLB')){
                  this.doubleLowerBerthSeats.forEach((seat, index, array) => {
                    if(seat.name === seatname){
                      array[index] = seatData;
                    }
                  });
                }
                this.cd.detectChanges();
                this.reload();
              }
            );
        }
      }
    });

  }

  reload(){
    this.allSeats = [];
    this.allSeats = this.allSeats.concat(
      this.singleUpperBerthSeats,
      this.doubleLowerBerthSeats,
      this.doubleUpperBerthSeats,
      this.lowerSingleSeats
    );
  }

  cancelSeat(seatName: string, seatprice: number) {
    const data = {
      seatStatus: 'Available',
      seatPrice: seatprice,
      passengerDetails: {
        passengerName: '',
        passengerAge: '',
        passengerGender: '',
      },
    };

    const seatData = {
      name: seatName,
      passengerDetails: {
        passengerName: '',
        passengerAge: '',
        passengerGender: '',
      },
      seatPrice: seatprice,
      seatStatus: 'Available',  
    };

    const busId = this.seatService.getBusData();

    this.http
      .put(
        'https://go-travel-42246-default-rtdb.firebaseio.com/busses/-' +
          busId +
          '/seats/' +
          seatName +
          '.json',
        data
      )
      .subscribe(
        (res) => {
          // console.log(dta.seatName + res+ 'success'+ busId);
          this.adjacent(seatName, seatprice);
          if(seatName.startsWith('DUB')){
            this.doubleUpperBerthSeats.forEach((seat, index, array) => {
              if(seat.name === seatName){
                array[index] = seatData;
              }
            });
          }
          if(seatName.startsWith('DLB')){
            this.doubleLowerBerthSeats.forEach((seat, index, array) => {
              if(seat.name === seatName){
                array[index] = seatData;
              }
            });
          }
          if(seatName.startsWith('LS')){
            this.lowerSingleSeats.forEach((seat, index, array) => {
              if(seat.name === seatName){
                array[index] = seatData;
              }
            });
          }
          if(seatName.startsWith('SUB')){
            this.singleUpperBerthSeats.forEach((seat, index, array) => {
              if(seat.name === seatName){
                array[index] = seatData;
              }
            });
          }
          this.cd.detectChanges();
          this.reload();
          window.alert('Ticket canceled successfully!');
        },
        (error) => {
          // console.log(error);
          window.alert('Error: ' + error.message);
        }
      );

  }

}
<div *ngIf="isAdmin">
  <nav class="navbar navbar-dark bg-primary navbar-expand">
    <ul class="navbar-nav">
      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="/admin">Admin</a>
      </li>
      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" routerLink="/addBus">Add Bus</a>
      </li>
      <li class="nav-item" routerLinkActive="active">
        <a class="nav-link" (click)="logout()">Logout</a>
      </li>
    </ul>
  </nav>
  </div>

  <div *ngIf="!isAdmin">
    <nav class="navbar navbar-dark bg-primary navbar-expand">
      <ul class="navbar-nav">
        <li class="nav-item" routerLinkActive="active">
          <a class="nav-link" routerLink="/user">User</a>
        </li>
        <li class="nav-item" routerLinkActive="active">
          <a class="nav-link"  (click)="logout()">Logout</a>
        </li>
      </ul>
    </nav>
</div>

<div class="container">
  <!-- <h2>Bus Seat Booking</h2> -->



  <div class="ctnr">
    <!-- Lower Single Seats -->
    <div class="seats">
      <div class="seat-container">
        <label
          *ngFor="let seat of lowerSingleSeats"
          class="seat"
          [class.admin]="isAdmin"
          [ngClass]="getSeatClass(seat.seatStatus)"
          (click)="toggleSeatStatus(seat)"
        >
          {{ seat.name }}
        </label>
      </div>

      <!-- Double Lower Berth Seats -->
      <div class="seat-container">
        <label
          *ngFor="let seat of doubleLowerBerthSeats; let i = index"
          class="berth"
          [class.admin]="isAdmin"
          [ngClass]="getSeatClass(seat.seatStatus)"
          [class.margin-left]="i < 6"
          (click)="toggleSeatStatus(seat)"
        >
          {{ seat.name }}
        </label>
      </div>
    </div>

    <div class="seats">
      <!-- Single Upper Berth Seats -->
      <div class="seat-container">
        <label
          *ngFor="let seat of singleUpperBerthSeats"
          class="berth"
          [class.admin]="isAdmin"
          [ngClass]="getSeatClass(seat.seatStatus)"
          (click)="toggleSeatStatus(seat)"
        >
          {{ seat.name }}
        </label>
      </div>

      <!-- Double Upper Berth Seats -->
      <div class="seat-container">
        <label
          *ngFor="let seat of doubleUpperBerthSeats; let i = index"
          class="berth"
          [class.admin]="isAdmin"
          [ngClass]="getSeatClass(seat.seatStatus)"
          [class.margin-left]="i < 6"
          (click)="toggleSeatStatus(seat)"
        >
          {{ seat.name }}
        </label>
      </div>
    </div>

    <div class="legend">
      <h3>LEGEND</h3>
      <ul>
        <li class="legend-item">
          <div class="legend-color booked"></div>
          <span>Booked</span>
        </li>
        <li class="legend-item">
          <div class="legend-color available"></div>
          <span>Available</span>
        </li>
        <li class="legend-item">
          <div class="legend-color ladies"></div>
          <span>Ladies</span>
        </li>
        <li class="legend-item">
          <div class="legend-color ladiesSelected"></div>
          <span>Ladies Selected</span>
        </li>
        <li class="legend-item">
          <div class="legend-color gents"></div>
          <span>Gents</span>
        </li>
        <li class="legend-item">
          <div class="legend-color gentsSelected"></div>
          <span>Gents Selected</span>
        </li>
        <li class="legend-item">
          <div class="legend-color selected"></div>
          <span>Selected</span>
        </li>
      </ul>
      <p>Abbreviations:</p>
      <ul>
        <li>LS - Lower Seat</li>
        <li>DLB - Double Lower Berth</li>
        <li>DUB - Double Upper Berth</li>
        <li>SLB - Single Upper Berth</li>
      </ul>
    </div>
  

  </div>
  <br />
  <br />
  <div *ngIf="selectedSeats.length > 0 && !isAdmin">
    <h4>Selected Seats</h4>
    <ul>
      <li *ngFor="let seat of selectedSeats">{{ seat.name }}</li>
    </ul>
    <button class="btn btn-primary" (click)="bookSelectedSeats()">
      Book Seats
    </button>
  </div>

  <div *ngIf="isAdmin">
    <div class="container">
      <table class="table table-bordered">
        <thead>
          <tr>
            <th>Seat Name</th>
            <th>Passenger Name</th>
            <th>Passenger Age</th>
            <th>Passenger Gender</th>
            <th>Seat Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          <ng-container *ngFor="let seat of allSeats">
            <tr *ngIf="seat.seatStatus === 'booked'">
              <td>{{ seat.name }}</td>
              <td>{{ seat.passengerDetails?.passengerName || "N/A" }}</td>
              <td>{{ seat.passengerDetails?.passengerAge || "N/A" }}</td>
              <td>{{ seat.passengerDetails?.passengerGender || "N/A" }}</td>
              <td>{{ seat.seatPrice || "N/A" }}</td>
              <td>
                <button class="btn btn-primary" (click)="cancelSeat(seat.name, seat.seatPrice)">Cancel Seat</button>
              </td>
            </tr>
          </ng-container>
        </tbody>
      </table>
    </div>
    
  </div>
</div>
