import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-admin-login',
  templateUrl: './admin-login.component.html',
  styleUrls: ['./admin-login.component.scss']
})
export class AdminLoginComponent {
  errorMessage: any;

  constructor(private router: Router, private authService: AuthService){}
  adminLogin!: FormGroup; 

  ngOnInit(){
  this.adminLogin = new FormGroup({
    username: new FormControl(null,[Validators.email,Validators.required]),
    password: new FormControl(null,[Validators.pattern('^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@#$%^&+=!])[a-zA-Z0-9@#$%^&+=!]{8,}$'),Validators.required])
  });
}
onSubmit() {
  if(this.adminLogin.valid){

    const email = this.adminLogin.value.username;
    const password = this.adminLogin.value.password;

    this.authService.signIn(email, password).subscribe(resData => {
      console.log(resData); 
      this.router.navigate(['/admin']);
    },
    error => {
      // console.log(error);
      this.errorMessage = error;
      this.adminLogin.reset();
    }
    );
  }
}

}
<div class="login-container">
  <img
    class="roll"
    src="../assets/bus-white.jpg"    alt="bus"
  />
  <div class="logo">Go Travel</div>
<div>
  <form [formGroup]="adminLogin" (submit)="onSubmit()">
    <div class="login-form">
      <nav class="navbar navbar-expand navbar-light">
        <ul class="navbar-nav">
          <li class="nav-item" routerLinkActive="active">
            <a class="nav-link" routerLink="/login">Login</a>
          </li>
          <li class="nav-item" routerLinkActive="active">
            <a class="nav-link" routerLink="/signup">Sign Up</a>
          </li>
          <li class="nav-item" routerLinkActive="active">
            <a class="nav-link" routerLink="/adminLogin">Admin</a>
          </li>
        </ul>
      </nav>
      
      <div class="form-group">
        <label for="username">Admin Name</label>
        <input
          type="text"
          id="username"
          placeholder="Enter your email"
          formControlName="username"
          class="form-control"
        />
        <span
          *ngIf="
            !adminLogin.get('username')?.valid &&
            adminLogin.get('username')?.touched
          "
          class="help-block"
          >Please Enter a valid UserName!</span
        >
      </div>
      <div class="form-group">
        <label for="password">Password</label>
        <input
          type="password"
          id="password"
          formControlName="password"
          placeholder="Enter your Password"
          class="form-control"
        />
        <span
          *ngIf="
            !adminLogin.get('password')?.valid &&
            adminLogin.get('password')?.touched
          "
          class="help-block"
          >Please Enter a valid Password!</span
        >
        <span
    *ngIf="errorMessage"
    class="help-block"
    >{{errorMessage}}</span
  >
      </div>
      <div class="btn-container">
        <button class="btn btn-dark" type="submit">Login</button>
        <!-- <span 
     *ngIf="!loginForm?.valid && loginForm?.touched"
     class="help-block">Please Enter valid Credentials!</span> -->
      </div>
    </div>
  </form>
</div>
